import { Component } from '@angular/core';
import { Writer } from '../models/Writer';
import { WritersService } from '../services/writers.service';

@Component({
  selector: 'app-writers',
  templateUrl: './writers.component.html',
  styleUrls: ['./writers.component.css']
})
export class WritersComponent {
  allWriters: Writer[] = [];

  constructor(private writerService :WritersService){

  }
  ngOnInit(): void {
    this.allWriters = this.writerService.getAllWriters()
  }

  sortByAwards(){
    this.allWriters = this.writerService.sortByAwards()
  }

  sortByBirth(){
    this.allWriters = this.writerService.sortByBirth()
  }
}
<p>writers works!</p>
<button>Sort awards</button>
<button>Sort birth</button>
<div>
    <tr>
        <th>Firstname</th>
        <th>Lastname</th>
        <th>Awards</th>
      </tr>
      <tr *ngFor="let writer of allWriters">
        <td>{{writer.firstname}}</td>
        <td>{{writer.lastname}}</td>
        <td>{{writer.awards}}</td>
      </tr>
</div>
