import { Component, OnInit } from '@angular/core';
import { User } from '../models/User';
import { UserService } from '../services/user.service';
import { Aktivnosti } from '../models/Aktivnosti';
import { Router } from '@angular/router';

@Component({
  selector: 'app-student',
  templateUrl: './student.component.html',
  styleUrls: ['./student.component.css']
})
export class StudentComponent implements OnInit{

  constructor(private userService:UserService,private router:Router){}
  ngOnInit(): void {
     let x = localStorage.getItem("logged");
     if(x!= null){
      this.student = JSON.parse(x);
     }
     this.userService.getAllAktivnosti().subscribe(
      data => {
        this.aktivnosti = data; 
      }
     )
  }

  student: User=new User();
  aktivnosti: Aktivnosti[]=[];


  aktivnostAkcija(a: Aktivnosti){
    localStorage.setItem("aktivnost",JSON.stringify(a));
  }
}
