  }
}
<table>
<tr *ngFor="let book of allBooks">
    <td>{{book.name}}</td>
    <td>{{book.likes}}
    <input type="checkbox" [(ngModel)]="book.toLike">
    </td>
</tr>
</table>
<button (click)="likeAll()">Like all</button>
