import { Component, OnInit } from '@angular/core';
import { User } from '../models/User';
import { Aukcija } from '../models/Aukcija';
import { AukcijeService } from '../services/aukcije.service';

@Component({
  selector: 'app-kupac',
  templateUrl: './kupac.component.html',
  styleUrls: ['./kupac.component.css']
})
export class KupacComponent implements OnInit{
  constructor(private aukcijeService:AukcijeService,private router:Router){

  }
  ngOnInit(): void {
    let x = localStorage.getItem("logg");
    if(x!=null){
      this.kupac=JSON.parse(x);
    }
    this.aukcijeService.getAllAukcije().subscribe(
      data=>{
        this.allAukcije = data;

      }
    )
  }

  kupac: User =new User();
  allAukcije: Aukcija[] = [];

  umetnineAkcije(a:Aukcija){
    localStorage.setItem("aukcija",JSON.stringify(a));
  }

}
