import { Component, OnInit } from '@angular/core';
import { Aktivnosti } from '../models/Aktivnosti';
import { AktivnostService } from '../services/aktivnost.service';
import { Prijave } from '../models/Prijave';

@Component({
  selector: 'app-aktivnost',
  templateUrl: './aktivnost.component.html',
  styleUrls: ['./aktivnost.component.css']
})
export class AktivnostComponent implements OnInit{  
  constructor(private aktivnostService:AktivnostService){}
  ngOnInit(): void {
    let x = localStorage.getItem("aktivnost");
    if(x!= null){
      this.aktivnost = JSON.parse(x);
    }
    this.aktivnostService.getAllPrijave(this.aktivnost.id).subscribe(
      data =>{
        this.prijave=data;
      }
    )
  }

  aktivnost:Aktivnosti = new Aktivnosti();
  prijave: Prijave= new Prijave();

}
