import { Router } from '@angular/router';
import { UserService } from './../services/user.service';
import { Component } from '@angular/core';
import { User } from '../models/User';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  message: string="";
  username: string=""; 
  password: string="";  
  tip: string="";
  constructor(private userService:UserService,private router:Router){}

  login(){
    if (this.username == "") {
      this.message = "Nije uneto korisnicko ime";
    }
    else if (this.password == "") {
      this.message = "Nije uneta lozinka";
    }
    this.userService.login(this.username,this.password).subscribe((userFromDB:User)=>{
      if(userFromDB!=null){
        if(userFromDB.type==0){
          this.router.navigate(['user']);
        }else{
          this.router.navigate(['admin']);
        }
      }
    })
  }


}
<div>
  <div class="absolute inset-0">
    <img src="/assets/images/auth/bg-gradient.png" alt="image" class="h-full w-full object-cover" />
  </div>

  <div class="relative flex min-h-screen items-center justify-center bg-[url(/assets/images/auth/map.png)] bg-cover bg-center bg-no-repeat px-6 py-10 dark:bg-[#060818] sm:px-16">
    <img src="/assets/images/auth/coming-soon-object1.png" alt="image" class="absolute left-0 top-1/2 h-full max-h-[893px] -translate-y-1/2" />
    <img src="/assets/images/auth/coming-soon-object2.png" alt="image" class="absolute left-24 top-0 h-40 md:left-[30%]" />
    <img src="/assets/images/auth/coming-soon-object3.png" alt="image" class="absolute right-0 top-0 h-[300px]" />
    <img src="/assets/images/auth/polygon-object.svg" alt="image" class="absolute bottom-0 end-[28%]" />
    <div
      class="relative w-full max-w-[870px] rounded-md bg-[linear-gradient(45deg,#fff9f9_0%,rgba(255,255,255,0)_25%,rgba(255,255,255,0)_75%,_#fff9f9_100%)] p-2 dark:bg-[linear-gradient(52.22deg,#0E1726_0%,rgba(14,23,38,0)_18.66%,rgba(14,23,38,0)_51.04%,rgba(14,23,38,0)_80.07%,#0E1726_100%)]"
    >
      <div class="relative flex flex-col justify-center rounded-md bg-white/60 px-6 py-20 backdrop-blur-lg dark:bg-black/50 lg:min-h-[758px]">
        <div class="absolute end-6 top-6">
          <div class="dropdown">
            <div hlMenu>
              <button
                type="button"
                hlMenuButton
                class="flex items-center gap-2.5 rounded-lg border border-white-dark/30 bg-white px-2 py-1.5 text-white-dark hover:border-primary hover:text-primary dark:bg-black"
              >
                <div>

                </div>

                <span class="shrink-0">

                </span>
              </button>

            </div>
          </div>
        </div>
        <div class="mx-auto w-full max-w-[440px]">
          <div class="mb-10">
            <h1 class="text-3xl font-extrabold uppercase !leading-snug text-primary md:text-4xl">log_in_title</h1>
            <p class="text-base font-bold leading-normal text-white-dark">log_in_description</p>
          </div>
          <form class="space-y-5 dark:text-white" [formGroup]="loginForm" (ngSubmit)="login()" novalidate>
            <div>
              <label for="Username">username</label>
              <div class="relative text-white-dark">
                <input id="Username" type="text" placeholder="Enter Username" class="form-input ps-10 placeholder:text-white-dark" formControlName="email" required />
                <span class="absolute start-4 top-1/2 -translate-y-1/2">

                </span>
              </div>
            </div>
            <div>
              <label for="Password">password</label>
              <div class="relative text-white-dark">
                <input id="Password" type="password" placeholder="Enter Password" class="form-input ps-10 placeholder:text-white-dark" formControlName="password" required />
                <span class="absolute start-4 top-1/2 -translate-y-1/2">

                </span>
              </div>
            </div>
            <button type="submit" class="btn btn-gradient !mt-6 w-full border-0 uppercase shadow-[0_10px_20px_-10px_rgba(67,97,238,0.44)]">

            </button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
