import { Router } from '@angular/router';
import { UserService } from './../services/user.service';
import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  message: string="";
  username: string=""; 
  password: string=""; 
  tip: string="";
  constructor(private userService:UserService,private router:Router){}

  login(){
    if (this.username == "") {
      this.message = "Nije uneto korisnicko ime";
    }
    else if (this.password == "") {
      this.message = "Nije uneta lozinka";
    }
    else if (this.tip == "") {
      this.message = "Nije unet tip";
    }
    this.userService.login(this.username,this.password,this.tip).subscribe(
      data=>{
        if(data == null){
          this.message = "Ne postoji ovaj korisnik u bazi!";
        }
        else{
          this.message="";
          localStorage.setItem("logg",JSON.stringify(data));
          if(data.tip =="kupac"){
            this.router.navigate(['kupac']);
          }
          else{
            this.router.navigate(['prodavac']);
          }
        }
    })
  }
}
