import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { User } from '../models/User';
import { Aktivnosti } from '../models/Aktivnosti';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http:HttpClient) { }

  login(korisnicko_ime:string,lozinka:string,tip:string){
    const d ={
      korisnicko_ime:korisnicko_ime,
      lozinka:lozinka,
      tip:tip
    }
    return this.http.post<User>("http://localhost:8080/users/login",d);
  }

  getAllAktivnosti(){
    return this.http.get<Aktivnosti[]>(`http://localhost:8080/users/aktivnosti`);
  }

}
