import { Component, OnInit } from '@angular/core';
import { Aukcija } from '../models/Aukcija';
import { AukcijeService } from '../services/aukcije.service';
import { Umetnine } from '../models/Umetnine';

@Component({
  selector: 'app-umetnine',
  templateUrl: './umetnine.component.html',
  styleUrls: ['./umetnine.component.css']
})
export class UmetnineComponent implements OnInit{

  constructor(private aukcijeService:AukcijeService){

  }
  ngOnInit(): void {
    let x =localStorage.getItem("aukcija");
    if(x!=null){
      this.aukcija=JSON.parse(x);
    }
    this.aukcijeService.getAllUmetnine(this.aukcija).subscribe(
      data=>{
        this.allUmetnine = data;
      }
    )

  }

  aukcija:Aukcija=new Aukcija();
  allUmetnine: Umetnine[] = [];
  message: string = "";
  novaponuda: number = 0;
  update: boolean = false;

  ponuda(u:Umetnine){

  }
}
