import { HttpClient } from '@angular/common/http';
import { Injectable, OnInit } from '@angular/core';
import { Book } from '../models/Book';

@Injectable({
  providedIn: 'root'
})
export class BooksService {

  back = "http://localhost:8080"
  constructor(private http:HttpClient) { }

  addBook(b: Book){
    return this.http.post<number>(`${this.back}/books/addBook`, b);
  }

  getAllBooks(){
    return this.http.get<Book[]>(`${this.back}/books/getAllBooks`);
  }

  likeBook(b:Book){
    return this.http.post<number>(`${this.back}/books/likeBook`,b);
  }

  deleteBook(b: Book) {
    return this.http.post<number>(`${this.back}/books/deleteBook`,b);
  }
}
