import { Component, OnInit } from '@angular/core';
import { User } from '../models/User';
import { Aukcija } from '../models/Aukcija';
import { AukcijeService } from '../services/aukcije.service';

@Component({
  selector: 'app-kupac',
  templateUrl: './kupac.component.html',
  styleUrls: ['./kupac.component.css']
})
export class KupacComponent implements OnInit{
  constructor(private aukcijeService:AukcijeService){

  }
  ngOnInit(): void {
    let x = localStorage.getItem("logg");
    if(x!=null){
      this.kupac=JSON.parse(x);
    }
    this.aukcijeService.getAllAukcije().subscribe(
      data=>{
        this.allAukcije = data;

      }
    )
  }

  kupac: User =new User();
  allAukcije: Aukcija[] = [];

  umetnineAkcije(a:Aukcija){
    localStorage.setItem("aukcija",JSON.stringify(a));
  }

}
Ime: {{kupac.ime}}<br>
Prezime: {{kupac.prezime}}<br>
Email: {{kupac.mejl}}<br>
<hr/>

<table border="1">
  <tr>
    <th>Naziv: </th>
    <th>Pocetak: </th>
    <th>Kraj: </th>
  </tr>
  <tr *ngFor="let a of allAukcije">
    <td><a routerLink="../umetnine" (click)="umetnineAkcije(a)">{{a.naziv}}</a></td>
    <td>{{a.pocetak | date: "short"}}</td>
    <td>{{a.kraj | date: "short"}}</td>
  </tr>
</table>
<hr/>
<app-logout></app-logout>
